const mongoose = require("mongoose");

const ResultSchema = new mongoose.Schema(
  {
    userId: {
      type: mongoose.Schema.Types.ObjectId,
      ref: "User",
      required: true,
      index: true,
    },

    sessionId: {
      type: mongoose.Schema.Types.ObjectId,
      ref: "QuizSession",
      required: true,
      index: true,
    },

    // Short summary generated by AI
    summary: {
      type: String,
      trim: true,
    },

    // Topics AI thinks the user should improve
    recommendedTopics: {
      type: [String],
      default: [],
    },

    // 7-day learning plan returned by AI
    learningPlan: {
      type: Object,
      default: {},
    },
  },
  { timestamps: true }
);

module.exports = mongoose.model("Result", ResultSchema);
